var keys = map[int]string{
	0: "",
	1: "",
	2: "",
	3: "",
	4: "",
}

var allKeys = joinKeys(keys)

func joinKeys(keys map[int]string) (s string) {
	for i := 0; i < len(keys); i++ {
		s += keys[i]
	}
	return
}

